import * as React from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Rating from '@mui/material/Rating';
import Avatar from '@mui/material/Avatar';
import { deepOrange, deepPurple } from '@mui/material/colors';
import Stack from '@mui/material/Stack';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import MessageIcon from '@mui/icons-material/Message';
import Link from '@mui/material/Link';
import { Button } from '@mui/material';

const defaultTheme = createTheme();
const commonStyles = {
    bgcolor: 'background.paper',
    m: 1,
    borderColor: 'text.primary',
    width: '800px',
    height: '600px',
};
const commonStyle = {
    bgcolor: 'background.paper',
    m: 1.5,
    borderColor: 'text.primary',
    width: '380px',
    height: '570px',
};

export default function ListNanny() {
    const [nannys, setNannys] = React.useState([]);

    const [currentPage, setCurrentPage] = React.useState(1);
    const itemsPerPage = 1;

    const handleNextPage = () => {
        const totalPages = Math.ceil(nannys.length / itemsPerPage);
        console.log(totalPages);
        setCurrentPage((prevPage) => (prevPage === totalPages ? prevPage : prevPage + 1));
    };

    const handlePrevPage = () => {
        setCurrentPage((prevPage) => (prevPage === 1 ? prevPage : prevPage - 1));
    };

    const startIndex = (currentPage - 1) * itemsPerPage;
    const visibleItems = nannys.slice(startIndex, startIndex + itemsPerPage);

    React.useEffect(() => {
        const fetchData = async () => {
            const reponse = await fetch(
                'https://babybuddies-be-dev.onrender.com/api/v1/getdata?fbclid=IwAR05wvwdZd0pOTBFqgnfEEBZKZCufSUf1BewzsNIoU05_IOAMByrcWu1FhA',
            );
            const reponseJSON = await reponse.json();
            setNannys(reponseJSON);
        };
        fetchData();
    }, []);

    // Tính tuổi
    function getAge(dateString) {
        var today = new Date();
        var birthDate = new Date(dateString);
        var age = today.getFullYear() - birthDate.getFullYear();
        var m = today.getMonth() - birthDate.getMonth();
        if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
            age--;
        }
        return age;
    }

    //lấy tên từ họ tên
    function getFirstName(fullName) {
        // Tách chuỗi thành mảng các từ
        var nameArray = fullName.split(' ');

        // Lấy phần tử cuối cùng trong mảng là tên
        var firstName = nameArray[nameArray.length - 1];

        return firstName;
    }

    // Conver chữ hoa dầu tiên của chuỗi
    function capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }

    function getCity(address) {
        // Tách chuỗi thành mảng các phần tử
        var addressArray = address.split(',');

        // Lấy phần tử thứ 3 trong mảng là thành phố
        var district = addressArray[addressArray.length - 2];
        var city = addressArray[addressArray.length - 1];
        var result = district.concat(',', city);
        return result;
    }

    // format ngày sinh
    function formatDate(dateString) {
        const date = new Date(dateString);
        const day = date.getDate();
        const month = date.getMonth() + 1;
        const year = date.getFullYear();

        const formattedDate = `${day < 10 ? '0' + day : day}-${month < 10 ? '0' + month : month}-${year}`;

        return formattedDate;
    }

    // tính số sao trung bình
    function calculateAverageRating(reviews) {
        var totalStars = 0;
        var totalReviews = reviews.length;

        for (var i = 0; i < totalReviews; i++) {
            totalStars += reviews[i].star;
        }

        var averageRating = totalStars / totalReviews;
        if (totalReviews === 0) return 0;
        else return averageRating;
    }

    // format số tiền 100000 => 100,000
    function formatNumber(number) {
        const formattedNumber = number.toLocaleString('en-US');
        return formattedNumber;
    }

    if (!nannys) return null;
    return (
        <ThemeProvider theme={defaultTheme}>
            <CssBaseline />
            <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2} sx={{ p: 4 }}>
                    {/* Details nanny  */}
                    <Grid item xs={7.5}>
                        <Grid container spacing={2}>
                            <Grid item xs={2.4}>
                                <Stack direction="column" spacing={2} sx={{ marginLeft: '120px', marginTop: '180px' }}>
                                    <Button disabled={currentPage === 1} onClick={handlePrevPage}>
                                        <Avatar
                                            sx={{
                                                width: '60px',
                                                height: '60px',
                                                backgroundColor: '#1a1aff',
                                                '&:hover': {
                                                    backgroundColor: '#5c5cf3',
                                                },
                                            }}
                                        >
                                            <ArrowBackIcon sx={{ fontSize: 30, color: 'white' }} />
                                        </Avatar>
                                    </Button>
                                    <Button underline="none" sx={{ color: 'white' }}>
                                        <Avatar
                                            sx={{
                                                bgcolor: deepOrange[500],
                                                width: '60px',
                                                height: '60px',
                                                fontSize: 30,
                                                backgroundColor: '#b3b300',
                                                '&:hover': {
                                                    backgroundColor: '#f0f04f',
                                                },
                                            }}
                                        >
                                            B.
                                        </Avatar>
                                    </Button>
                                    <Button>
                                        <Avatar
                                            sx={{
                                                bgcolor: deepOrange[500],
                                                width: '60px',
                                                height: '60px',
                                                backgroundColor: '#ff3300',
                                                '&:hover': {
                                                    backgroundColor: '#eb6a4a',
                                                },
                                            }}
                                        >
                                            <MessageIcon sx={{ fontSize: 30, color: 'white' }} />
                                        </Avatar>
                                    </Button>
                                    <Button
                                        disabled={currentPage === Math.ceil(nannys.length / itemsPerPage)}
                                        onClick={handleNextPage}
                                    >
                                        <Avatar
                                            sx={{
                                                bgcolor: deepPurple[500],
                                                width: '60px',
                                                height: '60px',
                                                backgroundColor: '#1a1aff',
                                                '&:hover': {
                                                    backgroundColor: '#5c5cf3',
                                                },
                                            }}
                                        >
                                            <ArrowForwardIcon sx={{ fontSize: 30, color: 'white' }} />
                                        </Avatar>
                                    </Button>
                                </Stack>
                            </Grid>

                            {visibleItems.map((nanny) => (
                                <Grid item xs={9.6} key={nanny.id}>
                                    <Box
                                        sx={{
                                            ...commonStyles,
                                            border: 1,
                                            borderRadius: '10px',
                                            display: 'flex',
                                            justifyContent: 'center',
                                            margin: 'auto',
                                        }}
                                    >
                                        <Box sx={{ ...commonStyle, border: 1 }}>
                                            <Typography
                                                variant="h5"
                                                sx={{ textAlign: 'center', margin: '15px' }}
                                                gutterBottom
                                                fontWeight="bold"
                                            >
                                                BABY BUDDIES
                                            </Typography>
                                            <Avatar
                                                sx={{
                                                    bgcolor: deepOrange[700],
                                                    width: '250px',
                                                    height: '250px',
                                                    margin: 'auto',
                                                }}
                                                alt="Remy Sharp"
                                                src="data:image/jpeg;base64,"
                                            />
                                            <Typography
                                                variant="h5"
                                                sx={{ textAlign: 'center', mt: '10px' }}
                                                gutterBottom
                                                fontWeight="bold"
                                            >
                                                {nanny.full_name}
                                            </Typography>
                                            <Typography
                                                variant="h6"
                                                sx={{ textAlign: 'center', mt: '10px' }}
                                                gutterBottom
                                            >
                                                Child Care Staff
                                            </Typography>
                                            <Typography
                                                variant="h6"
                                                sx={{ textAlign: 'center', mt: '10px' }}
                                                gutterBottom
                                            >
                                                Phone : {nanny.phone}
                                            </Typography>
                                            <Typography
                                                variant="h6"
                                                sx={{ textAlign: 'center', mt: '10px' }}
                                                gutterBottom
                                            >
                                                {capitalizeFirstLetter(nanny.gender)}
                                            </Typography>
                                            <Typography
                                                variant="h6"
                                                sx={{ textAlign: 'center', mt: '10px' }}
                                                gutterBottom
                                            >
                                                English , Japanese
                                            </Typography>

                                            <Rating
                                                readOnly
                                                value={calculateAverageRating(nanny.rating)}
                                                sx={{
                                                    justifyContent: 'flex-end',
                                                    display: 'flex',
                                                    marginRight: '15px',
                                                }}
                                            />
                                        </Box>
                                        <Box sx={{ ...commonStyle, border: 1 }}>
                                            <Typography
                                                variant="h5"
                                                sx={{ textAlign: 'center', margin: '15px' }}
                                                gutterBottom
                                                fontWeight="bold"
                                            >
                                                BABY BUDDIES
                                            </Typography>
                                            <Box sx={{ p: '15px' }}>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Name :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    {nanny.full_name}
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Gender :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    {capitalizeFirstLetter(nanny.gender)}
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Birthday :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    {formatDate(nanny.birthday)}
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Address :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    {getCity(nanny.address)}
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Experinence :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                    gutterBottom
                                                >
                                                    Cooking : {nanny.cook_exp}
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    Child Care : {nanny.care_exp}
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Language :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    English , Japanese
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Price :
                                                </Typography>
                                                <Typography
                                                    variant="h6"
                                                    sx={{
                                                        textAlign: '',
                                                        backgroundColor: '#e0ebeb',
                                                        borderRadius: '20px',
                                                        paddingLeft: '10px',
                                                    }}
                                                >
                                                    {formatNumber(nanny.salary)}VND /Day
                                                </Typography>
                                                <Typography variant="body1" sx={{ textAlign: '' }}>
                                                    Rating :
                                                    <Rating
                                                        readOnly
                                                        value={calculateAverageRating(nanny.rating)}
                                                        sx={{
                                                            justifyContent: 'flex-start',
                                                            display: 'flex',
                                                        }}
                                                    />
                                                </Typography>
                                            </Box>
                                        </Box>
                                    </Box>
                                </Grid>
                            ))}
                        </Grid>
                    </Grid>
                    {/* filter nanny  */}
                    <Grid item xs={4.5}>
                        <Box
                            sx={{
                                width: 600,
                                height: 300,
                                backgroundColor: 'primary.dark',
                                '&:hover': {
                                    backgroundColor: 'primary.main',
                                    opacity: [0.9, 0.8, 0.7],
                                },
                            }}
                        />
                    </Grid>
                </Grid>
            </Box>
        </ThemeProvider>
    );
}
